using Hemo.SendGrid.Properties;
using SendGrid;
using SendGrid.Helpers.Mail;
using System.Collections.Generic;
using System.Net;
using System.Text;

namespace Hemo.SendGrid
{
    public class SendGridSender : ISendGridSender
    {
        string[] englishMessages = new string[] { "Someone (hopefully you) has requested a password reset for your Hemo account.", "Reset code: ", "If you don't wish to reset your password, disregard this email and no action will be taken.", "Hemo Support" };
        string[] bulgarianMessages = new string[] { "Възстановяване на паролата беше активиранo за вашия Hemo акаунт.", "Код за възстановяване: ", "Ако не желаете да възстановите вашата парола, игнорирайте този имейл.", "Екип Hemo" };

        Dictionary<string, string[]> messagesPlain;

        SendGridClient client;

        public SendGridSender()
        {
            this.client = new SendGridClient(Settings.Default.ApiKey);
            this.messagesPlain = new Dictionary<string, string[]>();
            messagesPlain.Add("en", this.englishMessages);
            messagesPlain.Add("bg", this.bulgarianMessages);
        }

        public HttpStatusCode SendMessage(string fromEmail, string fromName, string toEmail, string subject, string plainTextContent, string htmlContent)
        {
            SendGridMessage message = this.ConstructMessage(fromEmail, fromName, toEmail, subject, plainTextContent, htmlContent);

            return this.SendSync(message);
        }

        private SendGridMessage ConstructMessage(string fromEmail, string fromName, string toEmail, string subject, string plainTextContent, string htmlContent)
        {
            SendGridMessage message = new SendGridMessage()
            {
                From = new EmailAddress(fromEmail, fromName),
                Subject = subject,
                PlainTextContent = plainTextContent,
                HtmlContent = htmlContent,
            };

            message.AddTo(toEmail);

            return message;
        }

        private HttpStatusCode SendSync(SendGridMessage message)
        {
            Response response = this.client.SendEmailAsync(message).Result;

            return response.StatusCode;
        }

        public string GetResetPasswordPlainText(string randomNumber, string language)
        {
            StringBuilder sb = new StringBuilder()
                .AppendLine(this.messagesPlain[language][0])
                .AppendLine()
                .AppendLine(this.messagesPlain[language][1] + randomNumber)
                .AppendLine()
                .AppendLine(this.messagesPlain[language][2])
                .AppendLine()
                .AppendLine(this.messagesPlain[language][3]);

            return sb.ToString();
        }

        public string GetResetPasswordHtml(string randomNumber, string language)
        {
            StringBuilder sb = new StringBuilder()
                .AppendLine($@"<!DOCTYPE html><html><body><img width=""200"" src=" + "data:image/jpeg;base64," + $@" /><p>" + this.messagesPlain[language][0] + "</p><p>" + this.messagesPlain[language][1] + $@"<b>{randomNumber}</b></p><p>" + this.messagesPlain[language][2] + "</p><br/><p>" + this.messagesPlain[language][3] + "</p></body></html>");

            return sb.ToString();
        }
    }
}
